SELECT EMPNAME, DEPT_NO FROM EMP_20BRS1193 WHERE DEPT_NO IN (SELECT DEPT_NO FROM DEPARTMENT_20BRS1193 WHERE DEPT_NO = 1001) ORDER BY EMPNAME;
SELECT EMPNAME, SALARY FROM EMP_20BRS1193 WHERE SALARY > (SELECT SALARY FROM EMP_20BRS1193 WHERE EMP_NO = 1234) ORDER BY EMPNAME;
SELECT EMP_NO, EMPNAME, JOB, SALARY, DEPT_NO FROM EMP_20BRS1193 WHERE SALARY > (SELECT AVG(SALARY) FROM EMP_20BRS1193 WHERE DEPT_NO = 1005) ORDER BY EMP_NO;
SELECT EMPNAME, SALARY FROM EMP_20BRS1193 WHERE SALARY = (SELECT MAX(SALARY) FROM EMP_20BRS1193);
SELECT * FROM (SELECT EMP_20BRS1193.EMPNAME, EMP_20BRS1193.SALARY FROM EMP_20BRS1193 WHERE EMP_20BRS1193.SALARY BETWEEN 20000 AND 40000) NEWTABLE; /* Unnecessarily long, below is a better version of the same query */
SELECT EMPNAME, SALARY FROM EMP_20BRS1193 WHERE SALARY BETWEEN 20000 AND 40000;
UPDATE EMP_20BRS1193 SET SALARY = SALARY*1.25 WHERE DEPT_NO = (SELECT DEPT_NO FROM DEPARTMENT_20BRS1193 WHERE DEPT_NAME = 'Market Research');
DELETE FROM EMP_20BRS1193 WHERE DEPT_NO = (SELECT DEPT_NO FROM DEPARTMENT_20BRS1193 WHERE DEPT_NAME = 'Admin');
SELECT EMP_20BRS1193.EMPNAME, DEPARTMENT_20BRS1193.DEPT_NAME FROM EMP_20BRS1193, DEPARTMENT_20BRS1193 WHERE EMP_20BRS1193.EMPNAME = (SELECT EMPNAME FROM EMP_20BRS1193 WHERE SALARY = (SELECT MIN(SALARY) FROM EMP_20BRS1193)) AND  DEPARTMENT_20BRS1193.DEPT_NAME = (SELECT DEPT_NAME FROM DEPARTMENT_20BRS1193 WHERE DEPT_NO = (SELECT DEPT_NO FROM EMP_20BRS1193 WHERE SALARY = (SELECT MIN(SALARY) FROM EMP_20BRS1193)));
SELECT EMPNAME, SALARY FROM EMP_20BRS1193 WHERE SALARY > (SELECT SALARY FROM EMP_20BRS1193 WHERE EMPNAME = 'Reena') AND DEPT_NO = (SELECT DEPT_NO FROM EMP_20BRS1193 WHERE EMPNAME = 'John'); 
SELECT EMPNAME, SALARY, DEPT_NO FROM EMP_20BRS1193 WHERE SALARY < (SELECT AVG(SALARY) FROM EMP_20BRS1193 WHERE DEPT_NO = 1001) ORDER BY EMPNAME;
SELECT COUNT(EMPNAME) FROM EMP_20BRS1193 WHERE DEPT_NO = (SELECT DEPT_NO FROM EMP_20BRS1193 WHERE EMPNAME = 'John'); 